function detectMaterialCodes(styleText) {
  if (!styleText) return [];
  const materialPatterns = [
    /YPG-\d+/gi, /YPA-\d+/gi, /YPW-\d+/gi, /SM-\d+/gi, /SG-\d+/gi,
    /CP-\d+/gi, /PW-\d+/gi, /LC-\d+/gi, /PL-\d+/gi, /PM-\d+/gi,
    /PE-\d+/gi, /AM-\d+/gi, /LP-\d+/gi, /HS\d+/gi, /HC\d+/gi,
    /HP\d+/gi, /HW\d+/gi, /MFB-\d+/gi, /LS-\d+/gi
  ];
  const detectedCodes = [];
  for (const pattern of materialPatterns) {
    const matches = styleText.match(pattern);
    if (matches) detectedCodes.push(...matches.map(m => m.toUpperCase()));
  }
  return [...new Set(detectedCodes)];
}

function detectColorKeywords(styleText) {
